pragma solidity 0.8.9;

import '../node_modules/@openzeppelin/contracts/token/ERC721/ERC721.sol';
import { IAxelarExecutable } from '../node_modules/@axelar-network/axelar-cgp-solidity/contracts/interfaces/IAxelarExecutable.sol';
import { StringToAddress, AddressToString } from './StringAddressUtils.sol';
import { IERC20 } from '../node_modules/@axelar-network/axelar-cgp-solidity/contracts/interfaces/IERC20.sol';
import { IAxelarGateway } from '../node_modules/@axelar-network/axelar-cgp-solidity/contracts/interfaces/IAxelarGateway.sol';

interface IAxelarGasReceiver {
    error NotOwner();
    error TransferFailed();
    error NothingReceived();
    error InvalidCodeHash();
    error SetupFailed();
    error NotProxy();

    event Upgraded(address indexed newImplementation);
    event OwnershipTransferred(address indexed newOwner);

    event GasPaidForContractCall(
        address indexed sourceAddress,
        string destinationChain,
        string destinationAddress,
        bytes32 indexed payloadHash,
        address gasToken,
        uint256 gasFeeAmount,
        address refundAddress
    );

    event GasPaidForContractCallWithToken(
        address indexed sourceAddress,
        string destinationChain,
        string destinationAddress,
        bytes32 indexed payloadHash,
        string symbol,
        uint256 amount,
        address gasToken,
        uint256 gasFeeAmount,
        address refundAddress
    );

    event NativeGasPaidForContractCall(
        address indexed sourceAddress,
        string destinationChain,
        string destinationAddress,
        bytes32 indexed payloadHash,
        uint256 gasFeeAmount,
        address refundAddress
    );

    event NativeGasPaidForContractCallWithToken(
        address indexed sourceAddress,
        string destinationChain,
        string destinationAddress,
        bytes32 indexed payloadHash,
        string symbol,
        uint256 amount,
        uint256 gasFeeAmount,
        address refundAddress
    );

    // Get current owner
    function owner() external view returns (address);

    // This is called on the source chain before calling the gateway to execute a remote contract.
    function payGasForContractCall(
        address sender,
        string calldata destinationChain,
        string calldata destinationAddress,
        bytes calldata payload,
        address gasToken,
        uint256 gasFeeAmount,
        address refundAddress
    ) external;

    // This is called on the source chain before calling the gateway to execute a remote contract.
    function payGasForContractCallWithToken(
        address sender,
        string calldata destinationChain,
        string calldata destinationAddress,
        bytes calldata payload,
        string calldata symbol,
        uint256 amount,
        address gasToken,
        uint256 gasFeeAmount,
        address refundAddress
    ) external;

    // This is called on the source chain before calling the gateway to execute a remote contract.
    function payNativeGasForContractCall(
        address sender,
        string calldata destinationChain,
        string calldata destinationAddress,
        bytes calldata payload,
        address refundAddress
    ) external payable;

    // This is called on the source chain before calling the gateway to execute a remote contract.
    function payNativeGasForContractCallWithToken(
        address sender,
        string calldata destinationChain,
        string calldata destinationAddress,
        bytes calldata payload,
        string calldata symbol,
        uint256 amount,
        address refundAddress
    ) external payable;

    function collectFees(address payable receiver, address[] calldata tokens) external;

    function refund(
        address payable receiver,
        address token,
        uint256 amount
    ) external;

    function setup(bytes calldata data) external;

    function upgrade(
        address newImplementation,
        bytes32 newImplementationCodeHash,
        bytes calldata params
    ) external;
}

contract NftLinker is ERC721, IAxelarExecutable {
    using StringToAddress for string;
    using AddressToString for address;

    error AlreadyInitialized();

    mapping(uint256 => bytes) public original; //abi.encode(originaChain, operator, tokenId);
    string chainName;   //To check if we are the source chain.
    IAxelarGasReceiver gasReceiver;

    constructor(string memory chainName_, address gateway_, address gasReceiver_) IAxelarExecutable(address(0)) ERC721('JUNCTION NFT Linker', 'JC') {
        gasReceiver = IAxelarGasReceiver(gasReceiver_);
        gateway = IAxelarGateway(gateway_);
        chainName = chainName_;
    }

    function init(string memory chainName_, address gateway_, address gasReceiver_) external {
        if(address(gateway) != address(0) || address(gasReceiver) != address(0)) revert AlreadyInitialized();
        gasReceiver = IAxelarGasReceiver(gasReceiver_);
        gateway = IAxelarGateway(gateway_);
        chainName = chainName_;
    }

    //The main function users will interract with.
    function sendNFT(
        address operator,  // NFT 컨트랙트 주소 or Linker 주소(0x7539F1999dCE56308468BEE4CB60990679d75945)
        uint256 tokenId, 
        string memory destinationChain, 
        address destinationAddress
    ) external payable {
        //If we are the operator then this is a minted token that lives remotely.
        if(operator == address(this)) {
            require(ownerOf(tokenId) == _msgSender(), 'NOT_YOUR_TOKEN');
            _sendMintedToken(tokenId, destinationChain, destinationAddress);
        } else {
            IERC721(operator).transferFrom(_msgSender(), address(this), tokenId);
            _sendNativeToken(operator, tokenId, destinationChain, destinationAddress);
        }
    }

    function mint(address to, uint256 tokenId) external {
        _mint(to, tokenId);
    }

    //Burns and sends a token.
    function _sendMintedToken(
        uint256 tokenId, 
        string memory destinationChain, 
        address destinationAddress
    ) internal {
        _burn(tokenId);
        //Get the original information.
        (
            string memory originalChain,
            address operator,
            uint256 originalTokenId
        ) = abi.decode(original[tokenId], (string, address, uint256));
        //Create the payload.
        bytes memory payload = abi.encode(originalChain, operator, originalTokenId, destinationAddress);
        string memory stringAddress = address(this).toString();
        //Pay for gas. We could also send the contract call here but then the sourceAddress will be that of the gas receiver which is a problem later.
        gasReceiver.payNativeGasForContractCall{value:msg.value}(
            address(this),
            destinationChain, 
            stringAddress, 
            payload,
            msg.sender
        );
        //Call the remote contract.
        gateway.callContract(
            destinationChain, 
            stringAddress, 
            payload
        );
    }
    //Locks and sends a token.
    function _sendNativeToken(
        address operator, 
        uint256 tokenId, 
        string memory destinationChain, 
        address destinationAddress
    ) internal {
        //Create the payload.
        bytes memory payload = abi.encode(chainName, operator, tokenId, destinationAddress);
        string memory stringAddress = address(this).toString();
        //Pay for gas. We could also send the contract call here but then the sourceAddress will be that of the gas receiver which is a problem later.
        gasReceiver.payNativeGasForContractCall{value: msg.value}(
            address(this),
            destinationChain, 
            stringAddress, 
            payload,
            msg.sender
        );
        //Call remote contract.
        gateway.callContract(
            destinationChain, 
            stringAddress, 
            payload
        );
    }

    //This is automatically executed by Axelar Microservices since gas was payed for.
    function _execute(string memory /*sourceChain*/, string memory sourceAddress, bytes calldata payload) internal override {
        //Check that the sender is another token linker.
        require(sourceAddress.toAddress() == address(this), 'NOT_A_LINKER');
        //Decode the payload.
        (
            string memory originalChain,
            address operator,
            uint256 tokenId,
            address destinationAddress
        ) = abi.decode(payload, (string, address ,uint256, address));
        //If this is the original chain then we give the NFT locally.
        if(keccak256(bytes(originalChain)) == keccak256(bytes(chainName))) {
            IERC721(operator).transferFrom(address(this), destinationAddress, tokenId);
        //Otherwise we need to mint a new one.
        } else {
            //We need to save all the relevant information.
            bytes memory originalData = abi.encode(originalChain, operator, tokenId);
            //Avoids tokenId collisions.
            uint256 newTokenId = uint256(keccak256(originalData));
            original[newTokenId] = originalData;
            _safeMint(destinationAddress, newTokenId);
        }
    }
}
